import java.awt.Component;
import java.net.MalformedURLException;
import java.net.URL;

import javax.swing.Icon;
import javax.swing.ImageIcon;
import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JOptionPane;

public class PhotoQuiz {

	public void run() throws Exception {

		JFrame quizWindow = new JFrame();
		quizWindow.setVisible(true);
		// This will make sure the program exits when you close the window
		quizWindow.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);

		// 1. find an image on the internet, and put its URL in a String
		// variable (from your browser, right click on the image, and select
		// â€œCopy Image Addressâ€�)
		String threecube = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRT7jM2NDxU6uO8YRxCMNWJNjOv3DPtId7ixIP0OzTJ-ObTEQBR9O1cxYpgbuBXaH77_yA_b20&usqp=CAc";
		// 2. create a variable of type "Component" that will hold your image
		Component comp = createImage(threecube);
		// 3. use the "createImage()" method below to initialize your Component

		// 4. add the image to the quiz window
		quizWindow.add(comp);
		// 5. call the pack() method on the quiz window
		quizWindow.pack();
		// 6. ask a question that relates to the image
		String question = JOptionPane.showInputDialog("what is the volume of this rubik's cube?");
		// 7. print "CORRECT" if the user gave the right answer
		if(question.equals("27")) {
			JOptionPane.showMessageDialog(null, "that is correct");
		}
		// 8. print "INCORRECT" if the answer is wrong
		else {
			JOptionPane.showMessageDialog(null, "that is incorrect");
		}
		// 9. remove the component from the quiz window (you may not see the
		// effect of this until step 12)
		quizWindow.remove(comp);
		// 10. find another image and create it (might take more than one line
		// of code)
		String twocube = "data:image/jpeg;base64,";
		Component comp2 = createImage(twocube);
		// 11. add the second image to the quiz window
		quizWindow.add(comp2);
		// 12. pack the quiz window
		quizWindow.pack();
		// 13. ask another question
		String question2 = JOptionPane.showInputDialog("what is the volume of the cube?");
		// 14+ check answer, say if correct or incorrect, etc.
		if(question2.equals("8")) {
			JOptionPane.showMessageDialog(null, "that is correct.");
		}
		else {
			JOptionPane.showMessageDialog(null, "that is incorrect.");
		}
	}

	private Component createImage(String imageUrl) throws MalformedURLException {
		URL url = new URL(imageUrl);
		Icon icon = new ImageIcon(url);
		JLabel imageLabel = new JLabel(icon);
		return imageLabel;
	}

	/* OPTIONAL */
	// *14. add scoring to your quiz
	// *15. make something happen when mouse enters image
	// (imageComponent.addMouseMotionListener())
}
